import React, { useEffect, useRef, useState } from 'react';
import { View, StyleSheet, Text, FlatList, TextInput, StatusBar, TouchableOpacity, Dimensions, ScrollView, Alert } from 'react-native'
import AsyncStorage from '@react-native-async-storage/async-storage'

import Modal from '../components/customModal'
import MaterialIcons from 'react-native-vector-icons/MaterialIcons'
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons'
import moment from "moment";

import * as Animatable from 'react-native-animatable'
import * as SQLite from 'expo-sqlite'
import * as Location from 'expo-location';
import Route from '../hooks/routes'

const bd = SQLite.openDatabase('localhost.db', '1.0')
const windowHeight = Dimensions.get('screen').height;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: 30,
        minHeight: Math.round(windowHeight),
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },

    image: {
        width: '100%',
        height: '100%',
        position: 'absolute',
        opacity: 0.3,
    },

    header: {
        flex: 0.5,
    },

    action: {
        padding: 10,
        flexDirection: 'row',
        width: '100%',
        marginBottom: 10,
        backgroundColor: '#fff',
        borderRadius: 10,

        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.2,
        shadowRadius: 8,
        elevation: 10,
    },

    descAction: {
        padding: 10,
        flexDirection: 'row',
        width: '100%',
        marginBottom: 10,
        backgroundColor: '#fff',
        borderRadius: 10,

        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.2,
        shadowRadius: 8,
        elevation: 10,
    },

    body: {
        flex: 10,
        width: '100%',
        height: '100%',
        //backgroundColor: 'red',
    },

    card: {
        backgroundColor: '#fff',
        width: '100%',
        height: '100%',
    },

    itemContainer: {
        margin: 10,
        padding: 10,
        backgroundColor: '#fff',
        borderRadius: 10,

        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.2,
        shadowRadius: 8,
        elevation: 10,
    },

    timeAction: {
        borderBottomColor: '#000',
        borderBottomWidth: 1,
        width: 30,
        height: 40,
        justifyContent: 'center',
        alignItems: 'center',
        fontSize: 14,
        fontWeight: 'bold',
        color: '#000',
    },

    labor: {
        padding: 10,
        backgroundColor: '#fff',
        borderRadius: 10,

        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.2,
        shadowRadius: 8,
        elevation: 10,
    },

    footer: {
        flex: 0.5,
        width: '100%',
        height: '100%',
        //backgroundColor: 'red'
    },
})

export default function detalleVehiculo({ navigation }) {
    const vehiculo = navigation.getParam('vehiculo', '0')
    const [faenaVehiculo, setFaenaVehiculo] = useState({})
    const [user, setUser] = useState(0)

    const [iniciarModal, setIniciarModal] = useState(false)
    const [loading, setLoading] = useState(true)

    const [btnIniciar, setBtnIniciar] = useState(true)
    const [btnDetencion, setBtnDetener] = useState(false)
    const [myLocation, setLocation] = useState({})

    const today = moment().format('YYYY-MM-DD')
    const date = new Date()
    const [hora, setHora] = useState(date.getHours() < 9 ? '0' + date.getHours() : date.getHours())
    const [minutos, setMinutos] = useState(date.getMinutes() < 9 ? '0' + date.getMinutes() : date.getMinutes())

    const getIdUsuario = (usuario) => {
        bd.transaction(tx => {
            tx.executeSql(
                'select id_usuario from usuario where nombre_usuario = ?;',
                [usuario],
                (tx, res) => {
                    setUser(res.rows._array[0].id_usuario)
                },
                (tx, error) => {
                    console.error(error.message)
                },
            )
        })
    }

    const getFaenaVehiculo = () => {
        bd.transaction(tx => {
            tx.executeSql(
                'SELECT * FROM faena_vehiculo WHERE id_vehiculo = ?;', [vehiculo.id_vehiculo],
                (tx, res) => {
                    setFaenaVehiculo(res.rows._array[0])

                    bd.transaction(tx => {
                        tx.executeSql(
                            'SELECT funcionando FROM funcionamiento_vehiculo WHERE id_faena_vehiculo = ? AND cerrada = 0;',
                            [res.rows._array[0].id_faena_vehiculo],
                            (tx, subRes) => {
                                if (subRes.rows.length == 0) {
                                    setBtnIniciar(true)
                                    setBtnDetener(false)
                                }
                                else if (subRes.rows._array[0].funcionando == 1) {
                                    setBtnIniciar(false)
                                    setBtnDetener(true)
                                }
                                else {
                                    setBtnIniciar(true)
                                    setBtnDetener(false)
                                }
                            },
                            (tx, error) => {
                                console.error(error.message)
                            },
                        )
                    })

                },
                (tx, error) => {
                    console.error(error.message)
                },
            )
        })
    }

    useEffect(() => {
        (async () => {
            let { status } = await Location.requestForegroundPermissionsAsync()
            if (status !== 'granted') {
                Alert.alert('Permisos Denegados', 'Es necesario saber su ubicaci贸n para que la Aplicaci贸n funcione.', [{
                    text: "Entendido",
                    onPress: () => navigation.replace('Sync')
                }])
            }
            else {
                let location = await Location.getCurrentPositionAsync({})
                setLocation(location)
            }
        })()
        getFaenaVehiculo()
        _loadUser()
    }, [])

    const _loadUser = async () => {
        try {
            const user = await AsyncStorage.getItem('user')
            getIdUsuario(user)
        }
        catch (e) {
            console.error('Error al traer los datos para el inicio de sesi贸n: ')
            console.error(e)

            Alert.alert(
                'Error al Iniciar Sesi贸n',
                'Hemos tenido un inconveniente recuperar los datos del Usuario',
                [{ text: 'Entendido', }]
            )
        }
    }

    setTimeout(() => {
        setLoading(false)
    }, 1000)

    return (
        <View animation={'fadeInUpBig'} style={styles.container}>
            <StatusBar barStyle='dark-content' translucent backgroundColor="transparent" />
            {!loading ? (
                <ScrollView style={styles.body}>
                    <View style={styles.card}>
                        <View style={{ paddingTop: 20, paddingLeft: 20, paddingRight: 20, justifyContent: 'center' }}>
                            <Text style={{ fontSize: 30, fontWeight: 'bold', color: '#000', marginBottom: 10, }}>Detalle del Veh铆culo:</Text>

                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'gray', marginTop: 10, }}>Informaci贸n General:</Text>
                            <View style={styles.itemContainer}>
                                <View style={{ marginBottom: 10, flexDirection: 'row', alignItems: 'center' }}>
                                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000' }}>Tipo de Vehiculo: </Text>
                                    <Text style={{ fontSize: 14, }}>{vehiculo.nombre_tipo_vehiculo}</Text>
                                </View>

                                <View style={{ marginBottom: 10, flexDirection: 'row', alignItems: 'center' }}>
                                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000' }}>Patente: </Text>
                                    <Text style={{ fontSize: 14, }}>{vehiculo.patente}</Text>
                                </View>

                                <View style={{ marginBottom: 10, flexDirection: 'row', alignItems: 'center' }}>
                                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000' }}>Proveedor: </Text>
                                    <Text style={{ fontSize: 14, }}>{vehiculo.nombre_proveedor}</Text>
                                </View>

                            </View>

                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'gray', marginTop: 10, }}>Procesos: </Text>
                            <View style={styles.itemContainer}>
                                <View style={{ alignItems: 'center', justifyContent: 'center', }}>
                                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000', marginLeft: 5, }}>Maquina en Funcionamiento:</Text>

                                    <View style={{ flexDirection: 'row', marginTop: 10, }}>
                                        <TouchableOpacity
                                            onPress={() => handleCambiarEstado('iniciar')}
                                            style={{ backgroundColor: btnIniciar ? 'green' : 'gray', width: 100, height: 30, borderRadius: 10, alignItems: 'center', justifyContent: 'center', marginRight: 10, }}>

                                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#fff' }}>Iniciar</Text>

                                        </TouchableOpacity>

                                        <TouchableOpacity
                                            onPress={() => handleCambiarEstado('detener')}
                                            style={{ backgroundColor: btnDetencion ? 'red' : 'gray', width: 100, height: 30, borderRadius: 10, alignItems: 'center', justifyContent: 'center' }}
                                        >

                                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#fff' }}>Detener</Text>
                                        </TouchableOpacity>
                                    </View>

                                </View>
                            </View>

                            <View style={styles.itemContainer}>
                                <TouchableOpacity>
                                    <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'center' }}>
                                        <View style={{ flex: 10, alignItems: 'center', justifyContent: 'center', flexDirection: 'row' }}>
                                            <MaterialCommunityIcons name='gas-station' color='#000' size={20} />
                                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000', marginLeft: 10 }}>Cargar Combustible</Text>
                                        </View>

                                        <View style={{ flex: 1, }}>
                                            <MaterialIcons name='keyboard-arrow-right' color='lightgray' size={20} />
                                        </View>
                                    </View>
                                </TouchableOpacity>
                            </View>

                            <View style={styles.itemContainer}>
                                <TouchableOpacity>
                                    <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'center' }}>
                                        <View style={{ flex: 10, alignItems: 'center', justifyContent: 'center', flexDirection: 'row' }}>
                                            <MaterialIcons name='report-problem' color='orange' size={20} />
                                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000', marginLeft: 10 }}>Reportar Problema</Text>
                                        </View>

                                        <View style={{ flex: 1, }}>
                                            <MaterialIcons name='keyboard-arrow-right' color='lightgray' size={20} />
                                        </View>
                                    </View>
                                </TouchableOpacity>
                            </View>

                        </View>
                    </View>
                </ScrollView>
            ) : (
                <Animatable.View animation={'fadeInUpBig'} style={{ alignItems: 'center', justifyContent: 'center' }}>
                    <Text style={{ fontSize: 28, fontWeight: 'bold' }}>Cargando...</Text>

                    <Animatable.View animation="tada" easing="ease-out" iterationCount="infinite">
                        <MaterialCommunityIcons name='dump-truck' size={40} color='#000' />
                    </Animatable.View>
                </Animatable.View>
            )}

        </View>
    )
}
