import React, { useEffect, useRef, useState } from 'react';
import { View, StyleSheet, Text, FlatList, TextInput, StatusBar, TouchableOpacity, Dimensions, ScrollView, Alert } from 'react-native'
import AsyncStorage from '@react-native-async-storage/async-storage'

import Modal from '../components/customModal'
import MaterialIcons from 'react-native-vector-icons/MaterialIcons'
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons'
import moment from "moment";

import * as Animatable from 'react-native-animatable'
import * as SQLite from 'expo-sqlite'
import * as Location from 'expo-location';
import Route from '../hooks/routes'

const bd = SQLite.openDatabase('localhost.db', '1.0')
const windowHeight = Dimensions.get('screen').height;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: 30,
        minHeight: Math.round(windowHeight),
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },

    image: {
        width: '100%',
        height: '100%',
        position: 'absolute',
        opacity: 0.3,
    },

    header: {
        flex: 0.5,
    },

    action: {
        padding: 10,
        flexDirection: 'row',
        width: '100%',
        marginBottom: 10,
        backgroundColor: '#fff',
        borderRadius: 10,

        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.2,
        shadowRadius: 8,
        elevation: 10,
    },

    descAction: {
        padding: 10,
        flexDirection: 'row',
        width: '100%',
        marginBottom: 10,
        backgroundColor: '#fff',
        borderRadius: 10,

        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.2,
        shadowRadius: 8,
        elevation: 10,
    },

    body: {
        flex: 10,
        width: '100%',
        height: '100%',
        //backgroundColor: 'red',
    },

    card: {
        backgroundColor: '#fff',
        width: '100%',
        height: '100%',
    },

    itemContainer: {
        margin: 10,
        padding: 10,
        backgroundColor: '#fff',
        borderRadius: 10,

        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.2,
        shadowRadius: 8,
        elevation: 10,
    },

    timeAction: {
        borderBottomColor: '#000',
        borderBottomWidth: 1,
        width: 30,
        height: 40,
        justifyContent: 'center',
        alignItems: 'center',
        fontSize: 14,
        fontWeight: 'bold',
        color: '#000',
    },

    labor: {
        padding: 10,
        backgroundColor: '#fff',
        borderRadius: 10,

        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.2,
        shadowRadius: 8,
        elevation: 10,
    },

    footer: {
        flex: 0.5,
        width: '100%',
        height: '100%',
        //backgroundColor: 'red'
    },
})

export default function detalleVehiculo({ navigation }) {
    const vehiculo = navigation.getParam('vehiculo', '0')
    const nextInput = useRef(null)
    const [faenaVehiculo, setFaenaVehiculo] = useState({})
    const [user, setUser] = useState(0)

    const [detenerModal, setDetenerModal] = useState(false)
    const [iniciarModal, setIniciarModal] = useState(false)
    const [loading, setLoading] = useState(true)

    const [tipoDetencion, setTipoDetencion] = useState([
        {
            nombre_tipo_detencion: 'DETENCION PARCIAL',
            value: true,
        },

        {
            nombre_tipo_detencion: 'DETENCION TOTAL',
            value: false,
        },
    ])

    const [motivoDetencion, setMotivoDetencion] = useState([])
    const [observacion, setObservacion] = useState('')
    const [selectedTipoDetencion, setSelectedTipoDetencion] = useState({})

    const [btnIniciar, setBtnIniciar] = useState(true)
    const [btnDetencion, setBtnDetener] = useState(false)
    const [myLocation, setLocation] = useState({})

    const [selectedLabor, setSelectedLabor] = useState({ nombre_labor: 'Seleccione Una Labor.' })
    const [labor, setLabor] = useState([])
    const [laborModal, setLaborModal] = useState(false)

    const [notifyError, setNotifyError] = useState(false)
    const [message, setMessage] = useState('')
    const [selectedMotivo, setSelectedMotivo] = useState({})

    const [report, setReport] = useState([])
    const [modalHorometro, setModalHorometro] = useState(false)
    const [horometro, setHorometro] = useState('')

    const today = moment().format('YYYY-MM-DD')
    const date = new Date()
    const [hora, setHora] = useState(date.getHours() < 9 ? '0' + date.getHours() : date.getHours())
    const [minutos, setMinutos] = useState(date.getMinutes() < 9 ? '0' + date.getMinutes() : date.getMinutes())

    const [trabajador, setTrabajador] = useState([])
    const [filteredTrabajador, setFilteredTrabajador] = useState([])
    const [selectedTrabajador, setSelectedTrabajador] = useState({})

    const [folio, setFolio] = useState('')
    const [folioModal, setFolioModal] = useState(false)
    const [IDcierre, setIDCierre] = useState({})

    const [busqueda, setBusqueda] = useState('')
    const [operadorModal, setOperadorModal] = useState(false)
    const filtrarTrabajador = (text) => {
        if (text) {
            const newData = trabajador.filter((item) => {
                const itemData = item.nombre_trabajador.toUpperCase();
                const textData = text.toUpperCase();

                return itemData.indexOf(textData) > -1;
            })
            setBusqueda(text)
            setFilteredTrabajador(newData)
        }
        else {
            setBusqueda(text)
            setFilteredTrabajador(trabajador)
        }
    }


    const handleIniciar = () => {
        if (hora >= 0 && hora < 24) {
            if (minutos >= 0 && minutos < 60) {
                verificarReport()
                setBtnIniciar(false)
                setBtnDetener(true)
                setNotifyError(false)
            }
            else {
                setNotifyError(true)
            }
        }
        else {
            setNotifyError(true)
        }
    }

    const getIdUsuario = (usuario) => {
        bd.transaction(tx => {
            tx.executeSql(
                'select id_usuario from usuario where nombre_usuario = ?;',
                [usuario],
                (tx, res) => {
                    setUser(res.rows._array[0].id_usuario)
                },
                (tx, error) => {
                    console.error(error.message)
                },
            )
        })
    }

    //** verificar si existe un report para este vehiculo_faena.
    //** en caso de que exista uno, se debe insertar una apertura para el detalle_report
    //** de otro modo, hay que generar un report para este faena_vehiculo y luego insertar la apertura en el detalle_report
    const verificarReport = async () => {
        setIniciarModal(false)

        const json = JSON.stringify({
            id_faena_vehiculo: faenaVehiculo.id_faena_vehiculo
        })

        await fetch(Route + 'verificar-report', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: json
        })
            .then(response => response.json())
            .then(data => {
                if (data.length == 0) {
                    setModalHorometro(true)
                }
                else {
                    const idreport = [{ insertar_report: data[0].id_report }]
                    setReport(data)
                    iniciarLabor(idreport)
                }
            })
            .catch(err => {
                console.log(err)
            })
    }

    //** generar un registro en caso de que no exista un report abierto para este faena_vehiculo
    const crearReport = async () => {
        setModalHorometro(false)

        const json = JSON.stringify({
            id_faena_vehiculo: faenaVehiculo.id_faena_vehiculo,
        })

        await fetch(Route + 'crear-report', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: json
        })
            .then(response => response.json())
            .then(data => {
                iniciarLabor(data)
                insertarHorometroInicial(data)
            })
            .catch(err => {
                console.log(err)
            })
    }

    const insertarHorometroInicial = async (idreport) => {
        const json = JSON.stringify({
            idreport: idreport[0].insertar_report,
            horometro: horometro == '' ? 0 : horometro,
        })

        console.log(json)

        await fetch(Route + 'insertar-horometro', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: json
        })
            .then(response => response.json())
            .then(data => {
                console.log(data)
            })
            .catch(err => {
                console.log(err)
            })
    }

    const iniciarLabor = async (idreport) => {
        const createdReport = idreport[0].insertar_report
        console.log(user)

        const json = JSON.stringify({
            idreport: createdReport,
            idlabor: 1,
            idusuario: user,
            fecha: moment().format('YYYY-MM-DD HH:mm:ss'),
            fechasupervisor: today + ' ' + hora + ':' + minutos,
            latitud: myLocation.coords.latitude + '',
            longitud: myLocation.coords.longitude + '',
        })

        console.log(json)

        await fetch(Route + 'iniciar-labor', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: json,
        })
            .then(response => response.json())
            .then(data => {
                console.log(data)
                //** pasar datos a bd local, apertura se realiza
                iniciarFuncionamientoVehiculo(idreport[0].insertar_report, data[0].inicio_labor)
            })
            .catch(err => {
                console.log(err.message)
            })
    }

    const iniciarFuncionamientoVehiculo = (idreport, idapertura) => {
        bd.transaction(tx => {
            tx.executeSql(
                'insert into funcionamiento_vehiculo(id_faena_vehiculo, id_report, funcionando, id_apertura, cerrada) values(?, ?, ?, ?, ?);',
                [faenaVehiculo.id_faena_vehiculo, idreport, true, idapertura, false],
                (tx, res) => {
                    console.log('Funcionamiento Vehiculo Insertado, RowsAffected: ' + res.rowsAffected)
                },
                (tx, error) => {
                    console.error(error.message)
                },
            )
        })
    }

    const getMotivoDetencion = async () => {
        await fetch(Route + 'motivo-detencion', { method: 'POST', })
            .then(response => response.json())
            .then(data => {
                setMotivoDetencion(data)
            })
            .catch(err => {
                console.log(err.message)
            })
    }

    const getLabor = () => {
        bd.transaction(tx => {
            tx.executeSql(
                'SELECT * FROM labor;', [],
                (tx, res) => {
                    setLabor(res.rows._array)
                },
                (tx, error) => {
                    console.error(error.message)
                },
            )
        })
    }

    const handleCambiarEstado = (button) => {
        if (button === 'iniciar' && !btnDetencion) {
            setIniciarModal(true)
        }
        else if (button === 'detener' && !btnIniciar) {
            setDetenerModal(true)
        }
    }

    const handleDetenerMaquina = () => {
        if (selectedTipoDetencion.value == true || selectedTipoDetencion.value == false) {
            setBtnDetener(false)
            setBtnIniciar(true)
            setDetenerModal(false)
            setLaborModal(true)
            setNotifyError(false)
        }
        else {
            setNotifyError(true)
        }
    }

    const detenerLabor = () => {
        if (selectedLabor.id_labor !== undefined) {

            bd.transaction(tx => {
                tx.executeSql(
                    'SELECT id_report, id_apertura FROM funcionamiento_vehiculo WHERE id_faena_vehiculo = ? and cerrada = 0;',
                    [faenaVehiculo.id_faena_vehiculo],
                    (tx, subRes) => {
                        setIDCierre(subRes.rows._array)
                        console.log(selectedTipoDetencion.value)

                        if (hora >= 0 && hora < 24) {
                            if (minutos >= 0 && minutos < 60) {
                                const json = JSON.stringify(
                                    {
                                        idreport: subRes.rows._array[0].id_report,
                                        idreportinicio: subRes.rows._array[0].id_apertura,
                                        idlabor: selectedLabor.id_labor,
                                        idusuario: user,
                                        fecha: moment().format('YYYY-MM-DD HH:mm:ss'),
                                        fechasupervisor: moment().format('YYYY-MM-DD')+' '+hora+':'+minutos,
                                        latitud: myLocation.coords.latitude + '',
                                        longitud: myLocation.coords.longitude + '',
                                        detencionParcial: selectedTipoDetencion.value,
                                    }
                                )
                                terminarLabor(json)
                                setHora(moment().format('HH'))
                                setMinutos(moment().format('mm'))
                                setSelectedLabor({})
                            }
                            else {
                                setNotifyError(true)
                            }
                        }
                        else {
                            setNotifyError(true)
                        }
                    },
                    (tx, error) => {
                        console.error(error.message)
                    },
                )
            })
        }
    }

    const terminarLabor = async (json) => {
        console.log(json)

        await fetch(Route + 'terminar-labor', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: json,
        })
            .then(response => response.json())
            .then(data => {
                if (data[0].cierre_labor !== undefined) {
                    updateFuncionamientoVehiculo()
                    setLaborModal(false)

                    console.log(selectedTipoDetencion.value)
                    if(selectedTipoDetencion.value == false){
                        console.log(selectedTipoDetencion.value)
                        setOperadorModal(true)
                    }
                }
            })
            .catch(err => {
                console.log(err.message)
            })
    }

    const cerrarReport = async () => {
        if (folio === '') {
            setNotifyError(true)
        }
        else {
            const json = JSON.stringify({
                idreport: IDcierre.id_report,
                folioreport: folio,
                horometrotermino: horometro,
            })

            await fetch(Route + 'cerrar-report', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: json,
            })
                .then(response => response.json())
                .then(data => {
                    console.log(data)
                    setFolio('')
                    setHorometro('')
                    setIDCierre({})
                })
                .catch(err => {
                    console.log(err.message)
                })
        }
    }

    const updateFuncionamientoVehiculo = () => {
        bd.transaction(tx => {
            tx.executeSql(
                'UPDATE funcionamiento_vehiculo SET funcionando = 0, cerrada = 1 WHERE id_faena_vehiculo = ?;',
                [faenaVehiculo.id_faena_vehiculo],
                (tx, res) => {
                    console.log(res)
                },       
                (tx, error) => {
                    console.error(error.message)
                },
            )
        })
    }

    const handleCancelar = (action) => {
        if (action === 'iniciar') {
            setIniciarModal(false)
        }
        else {
            setLaborModal(false)
            setDetenerModal(false)
            setFolioModal(false)
            setOperadorModal(false)

            setSelectedTipoDetencion({})
            setSelectedLabor({})
            setSelectedTrabajador({})

            setFolio('')
            setHorometro('')
        }
    }

    const getFaenaVehiculo = () => {
        bd.transaction(tx => {
            tx.executeSql(
                'SELECT * FROM faena_vehiculo WHERE id_vehiculo = ?;', [vehiculo.id_vehiculo],
                (tx, res) => {
                    setFaenaVehiculo(res.rows._array[0])

                    bd.transaction(tx => {
                        tx.executeSql(
                            'SELECT funcionando FROM funcionamiento_vehiculo WHERE id_faena_vehiculo = ? AND cerrada = 0;',
                            [res.rows._array[0].id_faena_vehiculo],
                            (tx, subRes) => {
                                if (subRes.rows.length == 0) {
                                    setBtnIniciar(true)
                                    setBtnDetener(false)
                                }
                                else if (subRes.rows._array[0].funcionando == 1) {
                                    setBtnIniciar(false)
                                    setBtnDetener(true)
                                }
                                else {
                                    setBtnIniciar(true)
                                    setBtnDetener(false)
                                }
                            },
                            (tx, error) => {
                                console.error(error.message)
                            },
                        )
                    })

                },
                (tx, error) => {
                    console.error(error.message)
                },
            )
        })
    }

    const getTrabajador = () => {
        bd.transaction(tx => {
            tx.executeSql(
                'select * from trabajador;',
                [],
                (tx, res) => {
                    setTrabajador(res.rows._array)
                    setFilteredTrabajador(res.rows._array)
                },
                (tx, error) => {
                    console.error(error.message)
                },
            )
        })
    }

    const handleOperador = () => {
        if (selectedTrabajador != {}) {
            setOperadorModal(false)
            setFolioModal(true)
            setNotifyError(false)
        }
        else {
            setNotifyError(true)
        }
    }

    useEffect(() => {
        (async () => {
            let { status } = await Location.requestForegroundPermissionsAsync()
            if (status !== 'granted') {
                Alert.alert('Permisos Denegados', 'Es necesario saber su ubicación para que la Aplicación funcione.', [{
                    text: "Entendido",
                    onPress: () => navigation.replace('Sync')
                }])
            }
            else {
                let location = await Location.getCurrentPositionAsync({})
                setLocation(location)
            }
        })()
        getFaenaVehiculo()
        _loadUser()
        getMotivoDetencion()
        getLabor()
        getTrabajador()
    }, [])

    const _loadUser = async () => {
        try {
            const user = await AsyncStorage.getItem('user')
            getIdUsuario(user)
        }
        catch (e) {
            console.error('Error al traer los datos para el inicio de sesión: ')
            console.error(e)

            Alert.alert(
                'Error al Iniciar Sesión',
                'Hemos tenido un inconveniente recuperar los datos del Usuario',
                [{ text: 'Entendido', }]
            )
        }
    }

    setTimeout(() => {
        setLoading(false)
    }, 1000)

    return (
        <View animation={'fadeInUpBig'} style={styles.container}>
            <StatusBar barStyle='dark-content' translucent backgroundColor="transparent" />
            {!loading ? (
                <ScrollView style={styles.body}>
                    <View style={styles.card}>
                        <View style={{ paddingTop: 20, paddingLeft: 20, paddingRight: 20, justifyContent: 'center' }}>
                            <Text style={{ fontSize: 30, fontWeight: 'bold', color: '#000', marginBottom: 10, }}>Detalle del Vehículo:</Text>

                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'gray', marginTop: 10, }}>Información General:</Text>
                            <View style={styles.itemContainer}>
                                <View style={{ marginBottom: 10, flexDirection: 'row', alignItems: 'center' }}>
                                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000' }}>Tipo de Vehiculo: </Text>
                                    <Text style={{ fontSize: 14, }}>{vehiculo.nombre_tipo_vehiculo}</Text>
                                </View>

                                <View style={{ marginBottom: 10, flexDirection: 'row', alignItems: 'center' }}>
                                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000' }}>Patente: </Text>
                                    <Text style={{ fontSize: 14, }}>{vehiculo.patente}</Text>
                                </View>

                                <View style={{ marginBottom: 10, flexDirection: 'row', alignItems: 'center' }}>
                                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000' }}>Proveedor: </Text>
                                    <Text style={{ fontSize: 14, }}>{vehiculo.nombre_proveedor}</Text>
                                </View>

                            </View>

                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'gray', marginTop: 10, }}>Procesos: </Text>
                            <View style={styles.itemContainer}>
                                <View style={{ alignItems: 'center', justifyContent: 'center', }}>
                                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000', marginLeft: 5, }}>Maquina en Funcionamiento:</Text>

                                    <View style={{ flexDirection: 'row', marginTop: 10, }}>
                                        <TouchableOpacity
                                            onPress={() => handleCambiarEstado('iniciar')}
                                            style={{ backgroundColor: btnIniciar ? 'green' : 'gray', width: 100, height: 30, borderRadius: 10, alignItems: 'center', justifyContent: 'center', marginRight: 10, }}>

                                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#fff' }}>Iniciar</Text>

                                        </TouchableOpacity>

                                        <TouchableOpacity
                                            onPress={() => handleCambiarEstado('detener')}
                                            style={{ backgroundColor: btnDetencion ? 'red' : 'gray', width: 100, height: 30, borderRadius: 10, alignItems: 'center', justifyContent: 'center' }}
                                        >

                                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#fff' }}>Detener</Text>
                                        </TouchableOpacity>
                                    </View>

                                </View>
                            </View>

                            <View style={styles.itemContainer}>
                                <TouchableOpacity>
                                    <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'center' }}>
                                        <View style={{ flex: 10, alignItems: 'center', justifyContent: 'center', flexDirection: 'row' }}>
                                            <MaterialCommunityIcons name='gas-station' color='#000' size={20} />
                                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000', marginLeft: 10 }}>Cargar Combustible</Text>
                                        </View>

                                        <View style={{ flex: 1, }}>
                                            <MaterialIcons name='keyboard-arrow-right' color='lightgray' size={20} />
                                        </View>
                                    </View>
                                </TouchableOpacity>
                            </View>

                            <View style={styles.itemContainer}>
                                <TouchableOpacity>
                                    <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'center' }}>
                                        <View style={{ flex: 10, alignItems: 'center', justifyContent: 'center', flexDirection: 'row' }}>
                                            <MaterialIcons name='report-problem' color='orange' size={20} />
                                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000', marginLeft: 10 }}>Reportar Problema</Text>
                                        </View>

                                        <View style={{ flex: 1, }}>
                                            <MaterialIcons name='keyboard-arrow-right' color='lightgray' size={20} />
                                        </View>
                                    </View>
                                </TouchableOpacity>
                            </View>

                            <View style={styles.itemContainer}>
                                <TouchableOpacity>
                                    <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'center' }}>
                                        <View style={{ flex: 10, alignItems: 'center', justifyContent: 'center', flexDirection: 'row' }}>
                                            <MaterialCommunityIcons name='clipboard-text' color='#000' size={20} />
                                            <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#000', marginLeft: 10 }}>Generar Report</Text>
                                        </View>

                                        <View style={{ flex: 1, }}>
                                            <MaterialIcons name='keyboard-arrow-right' color='lightgray' size={20} />
                                        </View>
                                    </View>
                                </TouchableOpacity>
                            </View>

                        </View>
                    </View>
                </ScrollView>
            ) : (
                <Animatable.View animation={'fadeInUpBig'} style={{ alignItems: 'center', justifyContent: 'center' }}>
                    <Text style={{ fontSize: 28, fontWeight: 'bold' }}>Cargando...</Text>

                    <Animatable.View animation="tada" easing="ease-out" iterationCount="infinite">
                        <MaterialCommunityIcons name='dump-truck' size={40} color='#000' />
                    </Animatable.View>
                </Animatable.View>
            )}

            <Modal visibility={detenerModal}>
                <View style={{ alignItems: 'center', justifyContent: 'center', }}>
                    <Text style={{ fontSize: 16, fontWeight: 'bold', color: '#000', marginBottom: 10, }}>Detener Máquina</Text>
                </View>

                <View style={{ flexDirection: 'row' }}>
                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'gray', marginBottom: 10, }}>Tipo de Detención </Text>
                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'red', marginBottom: 10, }}>*</Text>
                </View>

                <FlatList
                    data={tipoDetencion} ƒ
                    key={x => tipoDetencion.indexOf(x)}
                    keyExtractor={x => tipoDetencion.indexOf(x)}
                    renderItem={({ item }) => (
                        <TouchableOpacity
                            style={{
                                backgroundColor: selectedTipoDetencion == item ? 'green' : 'white',
                                borderRadius: 10,
                                padding: 10,
                                margin: 5,

                                shadowColor: '#000',
                                shadowOffset: { width: 0, height: 2 },
                                shadowOpacity: 0.2,
                                shadowRadius: 3,
                                elevation: 2,
                            }}
                            onPress={() => setSelectedTipoDetencion(item)}
                        >
                            <Text
                                style={{
                                    fontWeight: 'bold',
                                    fontSize: 15,
                                    color: selectedTipoDetencion == item ? 'white' : 'black',
                                }}>
                                {item.nombre_tipo_detencion}
                            </Text>
                        </TouchableOpacity>
                    )}
                />

                {
                    notifyError ? (
                        <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'red', marginBottom: 10, }} >¡Debe Seleccionar un Tipo de Detención!</Text>
                    ) : null
                }

                <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'center' }}>
                    <TouchableOpacity
                        onPress={() => handleDetenerMaquina()}
                        style={{ backgroundColor: 'green', alignItems: 'center', justifyContent: 'center', borderRadius: 10, width: 100, height: 30, marginRight: 10, }}
                    >
                        <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#fff' }}>Aceptar</Text>
                    </TouchableOpacity>

                    <TouchableOpacity
                        onPress={() => handleCancelar('detener')}
                        style={{ backgroundColor: 'red', alignItems: 'center', justifyContent: 'center', borderRadius: 10, width: 100, height: 30 }}
                    >
                        <Text style={{ fontSize: 14, fontWeight: 'bold', color: '#fff' }}>Cancelar</Text>
                    </TouchableOpacity>
                </View>

            </Modal>

            <Modal visibility={iniciarModal}>
                <Text style={{ alignSelf: 'center', fontSize: 22, fontWeight: 'bold', marginBottom: 20, }}>Iniciar</Text>

                <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'gray', marginBottom: 10, }}>Escriba la hora de arranque</Text>

                <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                    <View style={{ flexDirection: 'column', marginRight: 10 }}>
                        <TextInput
                            placeholder="Hh"
                            value={hora + ''}
                            returnKeyType='next'
                            keyboardType='numeric'
                            onSubmitEditing={() => nextInput.current.focus()}
                            style={{ borderBottomWidth: 1, borderBottomColor: '#000', fontSize: 18, fontWeight: 'bold' }}
                            onChangeText={(text) => setHora(text)}
                        />
                    </View>

                    <Text>:</Text>

                    <View style={{ flexDirection: 'column', marginLeft: 10 }}>
                        <TextInput
                            placeholder="Mm"
                            ref={nextInput}
                            keyboardType='numeric'
                            value={minutos + ''}
                            style={{ borderBottomWidth: 1, borderBottomColor: '#000', fontSize: 18, fontWeight: 'bold' }}
                            onChangeText={(text) => setMinutos(text)}
                        />
                    </View>
                </View>

                {
                    notifyError ? (
                        <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'red', marginBottom: 10, }}>Debe indicar un horario válido</Text>
                    ) : null
                }

                <View style={{ marginTop: 20, flexDirection: 'row', alignItems: 'center', justifyContent: 'center' }}>
                    <TouchableOpacity
                        onPress={() => handleIniciar()}
                        style={{ width: 100, height: 30, backgroundColor: 'green', borderRadius: 10, alignItems: 'center', justifyContent: 'center', marginRight: 10, }}>
                        <Text style={{ fontSize: 18, fontWeight: 'bold', color: '#fff' }}>Aceptar</Text>
                    </TouchableOpacity>

                    <TouchableOpacity
                        onPress={() => handleCancelar('iniciar')}
                        style={{ width: 100, height: 30, backgroundColor: 'red', borderRadius: 10, alignItems: 'center', justifyContent: 'center' }}>
                        <Text style={{ fontSize: 18, fontWeight: 'bold', color: '#fff' }}>Cancelar</Text>
                    </TouchableOpacity>
                </View>
            </Modal>

            <Modal visibility={modalHorometro}>
                <Text style={{ color: '#000', fontWeight: 'bold', fontSize: 18, marginBottom: 10, marginTop: 5 }}>Horometro o Kilometraje Inicial</Text>

                <TextInput
                    placeholder="Horometro (opcional)"
                    value={horometro}
                    keyboardType='numeric'
                    style={{ borderBottomWidth: 1, borderBottomColor: 'lightgray', fontSize: 18, fontWeight: 'bold' }}
                    onChangeText={(text) => setHorometro(text)}
                />

                <TouchableOpacity style={{
                    backgroundColor: 'green',
                    width: 100,
                    height: 30,
                    alignItems: 'center',
                    justifyContent: 'center',
                    alignSelf: 'center',
                    borderRadius: 10,
                    marginTop: 10,
                }}
                    onPress={() => crearReport()}
                >
                    <Text style={{ color: '#fff', fontWeight: 'bold', fontSize: 15, }}>Aceptar</Text>
                </TouchableOpacity>
            </Modal>

            <Modal visibility={laborModal}>
                <Text style={{ color: '#000', fontWeight: 'bold', fontSize: 18, marginBottom: 10, marginTop: 5, alignSelf: 'center' }}>Labor Realizada</Text>

                <FlatList
                    data={labor}
                    key={x => labor.indexOf(x)}
                    keyExtractor={x => labor.indexOf(x)}
                    renderItem={({ item }) => (
                        <TouchableOpacity
                            style={{
                                backgroundColor: selectedLabor == item ? 'green' : 'white',
                                borderRadius: 10,
                                padding: 10,
                                margin: 5,

                                shadowColor: '#000',
                                shadowOffset: { width: 0, height: 2 },
                                shadowOpacity: 0.2,
                                shadowRadius: 3,
                                elevation: 2,
                            }}
                            onPress={() => setSelectedLabor(item)}
                        >
                            <Text
                                style={{
                                    fontWeight: 'bold',
                                    fontSize: 15,
                                    color: selectedLabor == item ? 'white' : 'black',
                                }}>
                                {item.nombre_labor}
                            </Text>
                        </TouchableOpacity>
                    )}
                />

                <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'gray', marginBottom: 10, marginTop: 10,}}>Escriba la hora de termino</Text>

                <View style={{ flexDirection: 'row', alignItems: 'center', marginLeft: 10, }}>
                    <View style={{ flexDirection: 'column', marginRight: 10 }}>
                        <TextInput
                            placeholder="Hh"
                            value={hora + ''}
                            returnKeyType='next'
                            keyboardType='numeric'
                            onSubmitEditing={() => nextInput.current.focus()}
                            style={{ borderBottomWidth: 1, borderBottomColor: '#000', fontSize: 22, fontWeight: 'bold', marginBottom: 10 }}
                            onChangeText={(text) => setHora(text)}
                        />
                    </View>

                    <Text>:</Text>

                    <View style={{ flexDirection: 'column', marginLeft: 10 }}>
                        <TextInput
                            placeholder="Mm"
                            ref={nextInput}
                            keyboardType='numeric'
                            value={minutos + ''}
                            style={{ borderBottomWidth: 1, borderBottomColor: '#000', fontSize: 22, fontWeight: 'bold', marginBottom: 10}}
                            onChangeText={(text) => setMinutos(text)}
                        />
                    </View>
                </View>

                {notifyError ? (
                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'red', marginBottom: 10, }} >¡Debe Seleccionar un Tipo de Detención!</Text>
                ) : null}

                <View style={{ flexDirection: 'row', justifyContent: 'center', alignItems: 'center' }}>

                    <TouchableOpacity style={{
                        backgroundColor: 'green',
                        width: 100,
                        height: 30,
                        alignItems: 'center',
                        justifyContent: 'center',
                        alignSelf: 'center',
                        borderRadius: 10,
                        marginTop: 10,
                    }}
                        onPress={() => detenerLabor()}
                    >
                        <Text style={{ color: '#fff', fontWeight: 'bold', fontSize: 15, }}>Aceptar</Text>
                    </TouchableOpacity>

                    <TouchableOpacity style={{
                        backgroundColor: 'red',
                        width: 100,
                        height: 30,
                        alignItems: 'center',
                        justifyContent: 'center',
                        alignSelf: 'center',
                        borderRadius: 10,
                        marginLeft: 10,
                        marginTop: 10,
                    }}
                        onPress={() => handleCancelar('cancelar')}
                    >
                        <Text style={{ color: '#fff', fontWeight: 'bold', fontSize: 15, }}>Cancelar</Text>
                    </TouchableOpacity>

                </View>


            </Modal>

            <Modal visibility={operadorModal}>
                <Text style={{ fontSize: 18, fontWeight: 'bold', color: '#000', marginBottom: 10, alignSelf: 'center' }}>Operador</Text>
                <View style={styles.action}>
                    <MaterialIcons color="gray" name="search" size={20} style={{ flex: 1, marginRight: 10, alignSelf: 'center' }} />
                    <TextInput
                        placeholder="Buscar Operador"
                        style={{ width: '100%', flex: 10 }}
                        onChangeText={(text) => filtrarTrabajador(text)}
                        value={busqueda} />
                </View>

                <FlatList
                    data={filteredTrabajador}
                    key={(x) => filteredTrabajador.indexOf(x)}
                    keyExtractor={(x) => filteredTrabajador.indexOf(x)}
                    renderItem={({ item }) => (
                        <View style={[styles.itemContainer, { backgroundColor: selectedTrabajador == item ? "green" : "#fff" }]}>
                            <TouchableOpacity onPress={() => setSelectedTrabajador(item)}>
                                <View style={{ flexDirection: 'row', width: '100%', }}>
                                    <MaterialIcons name="fiber-manual-record" color={selectedTrabajador == item ? "#fff" : "green"} size={10} style={{ flex: 1, alignSelf: 'center' }} />
                                    <Text style={{ flex: 15, fontSize: 14, fontWeight: 'bold', color: selectedTrabajador == item ? "#fff" : "#000" }}>{item.nombre_trabajador}</Text>
                                </View>
                            </TouchableOpacity>
                        </View>
                    )}
                />

                {notifyError ? (
                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'red', marginBottom: 10, }} >¡Debe Seleccionar un Operador!</Text>
                ) : null}

                <View style={{ flexDirection: 'row', justifyContent: 'center', alignItems: 'center' }}>

                    <TouchableOpacity style={{
                        backgroundColor: 'green',
                        width: 100,
                        height: 30,
                        alignItems: 'center',
                        justifyContent: 'center',
                        alignSelf: 'center',
                        borderRadius: 10,
                        marginTop: 10,
                    }}
                        onPress={() => handleOperador()}
                    >
                        <Text style={{ color: '#fff', fontWeight: 'bold', fontSize: 15, }}>Aceptar</Text>
                    </TouchableOpacity>

                    <TouchableOpacity style={{
                        backgroundColor: 'red',
                        width: 100,
                        height: 30,
                        alignItems: 'center',
                        justifyContent: 'center',
                        alignSelf: 'center',
                        borderRadius: 10,
                        marginLeft: 10,
                        marginTop: 10,
                    }}
                        onPress={() => handleCancelar('cancelar')}
                    >
                        <Text style={{ color: '#fff', fontWeight: 'bold', fontSize: 15, }}>Cancelar</Text>
                    </TouchableOpacity>

                </View>


            </Modal>

            <Modal visibility={folioModal}>
                <Text style={{ fontSize: 18, fontWeight: 'bold', color: '#000', marginBottom: 20, alignSelf: 'center' }}>Detalles Finales</Text>

                <Text style={{ fontSize: 15, fontWeight: 'bold', color: '#000', marginBottom: 10 }}>Folio del Report</Text>
                <View style={styles.action}>
                    <MaterialCommunityIcons color="gray" name="pencil-plus-outline" size={20} style={{ flex: 1, marginRight: 10, alignSelf: 'center' }} />
                    <TextInput
                        placeholder="Num. Folio"
                        style={{ width: '100%', flex: 10 }}
                        keyboardType='numeric'
                        onChangeText={(text) => setFolio(text)}
                        value={null} />
                </View>

                <Text style={{ fontSize: 15, fontWeight: 'bold', color: '#000', marginBottom: 10 }}>Horometro Final o Kilometraje</Text>
                <View style={styles.action}>
                    <MaterialCommunityIcons color="gray" name="timer-outline" size={20} style={{ flex: 1, marginRight: 10, alignSelf: 'center' }} />
                    <TextInput
                        placeholder="Horometro o Kilometraje (opcional)"
                        style={{ width: '100%', flex: 10 }}
                        keyboardType='numeric'
                        onChangeText={(text) => setHorometro(text)}
                        value={{}} />
                </View>

                {notifyError ? (
                    <Text style={{ fontSize: 14, fontWeight: 'bold', color: 'red', marginBottom: 10, }} >¡Debe Ingresar el Folio del Report!</Text>
                ) : null}

                <View style={{ flexDirection: 'row', justifyContent: 'center', alignItems: 'center' }}>

                    <TouchableOpacity style={{
                        backgroundColor: 'green',
                        width: 100,
                        height: 30,
                        alignItems: 'center',
                        justifyContent: 'center',
                        alignSelf: 'center',
                        borderRadius: 10,
                        marginTop: 10,
                    }}
                        onPress={() => cerrarReport()}
                    >
                        <Text style={{ color: '#fff', fontWeight: 'bold', fontSize: 15, }}>Aceptar</Text>
                    </TouchableOpacity>

                    <TouchableOpacity style={{
                        backgroundColor: 'red',
                        width: 100,
                        height: 30,
                        alignItems: 'center',
                        justifyContent: 'center',
                        alignSelf: 'center',
                        borderRadius: 10,
                        marginLeft: 10,
                        marginTop: 10,
                    }}
                        onPress={() => handleCancelar('cancelar')}
                    >
                        <Text style={{ color: '#fff', fontWeight: 'bold', fontSize: 15, }}>Cancelar</Text>
                    </TouchableOpacity>

                </View>


            </Modal>

        </View>
    )
}
