import React, { useState, useEffect, useCallback } from 'react'
import { View, StyleSheet, Text, ActivityIndicator, Alert, StatusBar, TouchableOpacity, FlatList, TextInput } from 'react-native'

import * as Animatable from 'react-native-animatable'
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons'
import MaterialIcons from 'react-native-vector-icons/MaterialIcons'

import ruta from '../hooks/routes'
import AsyncStorage from '@react-native-async-storage/async-storage'
import * as SQLite from 'expo-sqlite'

import Modal from '../components/customModal'

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },

    header: {
        flex: 1,
        width: '100%',
    },

    searchBar: {
        width: '80%',
        height: '30%',
        borderBottomWidth: 1,
        borderBottomColor: '#fff',
        flexDirection: 'row',
    },

    body: {
        flex: 10,
    },

    footer: {
        flex: 1,
    },
})

const bd = SQLite.openDatabase('localhost.db', '1.0')

export default function faenas({ navigation }) {
    const [loading, setLoading] = useState(false)
    const [retry, setRetry] = useState(false)
    const [mensaje, setMensaje] = useState('')

    const createTables = () => {
        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS usuario (id_usuario INTEGER PRIMARY KEY AUTOINCREMENT, nombre_usuario VARCHAR(100), clave VARCHAR(100), id_trabajador INTEGER);', [],
                (tx, res) => {
                    console.log('tabla usuario creada')
                },
                (tx, error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar los datos del Usuario')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS trabajador (id_trabajador INTEGER, id_tipo_trabajador INTEGER, rut_trabajador INTEGER, dv_trabajador VARCHAR(100), nombre_trabajador VARCHAR(100), direccion_trabajador VARCHAR(100), id_comuna INTEGER, fono_trabajador VARCHAR(100), mail_trabajador VARCHAR(100));', [],
                (tx, res) => {
                    console.log('tabla trabajador creada')
                },
                (tx, error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar los datos de los Trabajadores')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS faena (id_faena INTEGER, nombre_faena VARCHAR(100), id_trabajador INTEGER, id_comuna INTEGER, vigente BOOLEAN);', [],
                (tx, res) => {
                    console.log('tabla faena creada')
                },
                (tx, error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar las Faenas')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS comuna (id_comuna INTEGER, nombre_comuna VARCHAR(100));', [],
                (tx, res) => {
                    console.log('tabla comuna creada')
                },
                (tx, error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar las Comunas')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS vehiculo(id_vehiculo INTEGER, patente VARCHAR(100), id_tipo_vehiculo INTEGER, id_estado_vehiculo INTEGER, id_trabajador INTEGER, id_proveedor INTEGER, modelo VARCHAR(200), anio VARCHAR(100), vim VARCHAR(100), numero_motor VARCHAR(200), observaciones VARCHAR(2000), marca VARCHAR(100), vigente BOOLEAN, horometro INTEGER DEFAULT 0);', [],
                (tx, res) => {
                    console.log('tabla vehiculo creada')
                },
                (tx, error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar los Vehiculos')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS faena_vehiculo(id_faena_vehiculo INTEGER, id_vehiculo INTEGER, id_faena INTEGER, fecha_inicio DATE, fecha_termino DATE, motivo_termino VARCHAR(100), vigente BOOLEAN, id_trabajador INTEGER, observaciones VARCHAR(2000));', [],
                (tx, res) => {
                    console.log('tabla faena_vehiculo creada')
                },
                (tx, error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar los datos Faena-Vehiculo')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS tipo_trabajador(id_tipo_trabajador INTEGER, nombre_tipo_trabajador VARCHAR(100));', [],
                (tx, res) => {
                    console.log('tabla tipo_trabajador creada')
                },
                (tx, error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar los Tipos de Trabajadores')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS tipo_vehiculo(id_tipo_vehiculo INTEGER, nombre_tipo_vehiculo VARCHAR(100));', [],
                (tx, res) => {
                    console.log('tabla tipo_vehiculo creada')
                },
                (tx, error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar los Tipos de Vehiculos')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS proveedor(id_proveedor INTEGER, rut_proveedor INTEGER, dv_proveedor VARCHAR(100), nombre_proveedor VARCHAR(200), giro_proveedor VARCHAR(200), direccion_proveedor VARCHAR(200), id_comuna INTEGER, fono_proveedor VARCHAR(200), mail_proveedor VARCHAR(200));', [],
                (tx, res) => {
                    console.log('tabla proveedor creada')
                },
                (error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar los Proveedores')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS labor(id_labor INTEGER, nombre_labor VARCHAR(200));', [],
                (tx, res) => {
                    console.log('tabla labor creada')
                },
                (error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar los Proveedores')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS report(id_report INTEGER PRIMARY KEY AUTOINCREMENT, id_faena_vehiculo INTEGER, folio_report INTEGER, id_report_sistema INTEGER);', [],
                (tx, res) => {
                    console.log('tabla report creada')
                },
                (error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar los report')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS detalle_report(id_detalle_report INTEGER PRIMARY KEY AUTOINCREMENT, id_report INTEGER, id_report_apertura INTEGER, id_labor INTEGER, id_usuario INTEGER, fecha_hora_supervisor VARCHAR(200), fecha_hora VARCHAR(200), latitud VARCHAR(2000), longitud VARCHAR(2000), con_cierre BOOLEAN, id_motivo_detencion INTEGER, observaciones VARCHAR(2000), horometro VARCHAR(100));', [],
                (tx, res) => {
                    console.log('tabla detalle_report creada')
                },
                (error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar los detalle_report')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS funcionamiento_vehiculo(id_funcionamiento_vehiculo INTEGER PRIMARY KEY AUTOINCREMENT, id_faena_vehiculo INTEGER, id_report INTEGER, funcionando BOOLEAN, id_apertura INTEGER DEFAULT 0, cerrada BOOLEAN DEFAULT 0);', [],
                (tx, res) => {
                    console.log('tabla funcionamiento_vehiculo creada')
                },
                (error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar los funcionamiento_vehiculo')
                    console.error(error.message)
                },
            )
        })

        bd.transaction(tx => {
            tx.executeSql(
                'CREATE TABLE IF NOT EXISTS tipo_detencion(id_tipo_detencion INTEGER, nombre_tipo_detencion VARCHAR(100));', [],
                (tx, res) => {
                    console.log('tabla tipo_detencion creada')
                },
                (error) => {
                    setRetry(true)
                    setLoading(false)
                    setMensaje('Error al guardar losd datos de tipo_detencion')
                    console.error(error.message)
                },
            )
        })

    }

    const selectComuna = (data) => {
        bd.transaction(tx => {
            tx.executeSql(
                'select * from comuna', [],
                (tx, res) => {
                    const localComunas = res.rows

                    for (let i = 0; i < data.length; i++) {
                        if (localComunas.length == 0) {
                            bd.transaction(tr => {
                                tr.executeSql(
                                    'insert into comuna(id_comuna, nombre_comuna) values(?,?)',
                                    [data[i].id_comuna, data[i].nombre_comuna],
                                    (tr, res) => {
                                        console.log('comuna insertada: ' + data[i].nombre_comuna)
                                    },
                                    (tr, e) => {
                                        console.error(e)
                                    }
                                )
                            })
                        }
                        else if (localComunas._array[i].id_comuna != data[i].id_comuna) {
                            bd.transaction(tr => {
                                tr.executeSql(
                                    'insert into comuna(nombre_comuna) values(?)',
                                    [data[i].nombre_comuna],
                                    (tr, res) => {
                                        console.log('comuna insertada: ' + data[i].nombre_comuna)
                                    },
                                    (tr, e) => {
                                        console.error(e)
                                    }
                                )
                            })
                        }
                        else {
                            null
                        }
                    }
                },
                (error) => {
                    console.error(error.message)
                },
            )
        })
    }

    const selectUsuario = (data) => {
        bd.transaction(tx => {
            tx.executeSql(
                'select * from usuario', [],
