                                    [data[i].id_faena_vehiculo, data[i].id_vehiculo, data[i].id_faena, data[i].fecha_inicio, data[i].fecha_termino, data[i].motivo_termino, data[i].vigente, data[i].id_trabajador, data[i].observaciones],
