                                        console.error(e)
                                    }
                                )
                            })
                        }
                        else if (localFaena._array[i].id_faena != data[i].id_faena) {
                            bd.transaction(tr => {
                                tr.executeSql(
                                    'insert into faena_vehiculo(id_faena_vehiculo, id_vehiculo, id_faena, fecha_inicio, fecha_termino, motivo_termino, vigente, id_trabajador, observaciones) values(?, ?, ?, ?, ?, ?, ?, ?, ?)',
                                    [data[i].id_faena_vehiculo, data[i].id_vehiculo, data[i].id_faena, data[i].fecha_inicio, data[i].fecha_termino, data[i].motivo_termino, data[i].vigente, data[i].id_trabajador, data[i].observaciones],
