    const selectTipoVehiculo = (data) => {
        bd.transaction(tx => {
            tx.executeSql(
                'select * from tipo_vehiculo', [],
                (tx, res) => {
                    const localTipoVehiculo = res.rows

                    for (let i = 0; i < data.length; i++) {
                        if (localTipoVehiculo.length == 0) {
                            bd.transaction(tr => {
                                tr.executeSql(
                                    'insert into tipo_vehiculo(id_tipo_vehiculo, nombre_tipo_vehiculo ) values(?,?)',
                                    [data[i].id_tipo_vehiculo, data[i].nombre_tipo_vehiculo],
                                    (tr, res) => {
                                        console.log('tipo vehiculo insertado: ' + data[i].nombre_tipo_trabajador)
                                    },
                                    (tr, e) => {
                                        console.error(e)
                                    }
                                )
                            })
                        }
                        else if (localTipoVehiculo._array[i].id_comuna != data[i].id_comuna) {
                            bd.transaction(tr => {
                                tr.executeSql(
                                    'insert into tipo_trabajador(id_tipo_trabajador, nombre_tipo_trabajador ) values(?,?)',
                                    [data[i].id_tipo_trabajador, data[i].nombre_tipo_trabajador],
